function parentRowIndex(path) {
  // Split the path by dots to get individual parts
  const parts = path.split('.');

  // Extract the first as the parent
  const parent = parts[0]; //parts.length - 3

  // Find the index of '[' and ']'
  const startIndex = parent.indexOf('[');
  const endIndex = parent.indexOf(']');

  // Extract the rowIndex between '[' and ']'
  const rowIndex = parent.substring(startIndex + 1, endIndex);

  return rowIndex;
}

